import dataset from "../data/data";
import { useState, useEffect } from "react";
const Rewardspoint = () => {
  const [data, setData] = useState([]);
  useEffect(() => {
    setTimeout(() => {
      setData(dataset);
    }, 500);
  }, []);
  setData(
    data.map((ele) => {
      ele.Rewardspoint = CalculatePoint(ele.purchasecost);
    })
  );
  const CalculatePoint = (amount) => {
    let point = 0;
    if (amount > 50 && amount <= 100) {
      point += (amount - 50) * 1;
    } else if (amount > 100) {
      point += (amount - 100) * 2 + 50 * 1;
    } else {
      point += 0;
    }
    return point;
  };

  return (
    <div className="datatable">
      <table>
        <tr>
          <th>Customer</th>
          <th>Purchase Amount</th>
          <th>Date</th>
          <th>Reward points</th>
        </tr>
        {data.map((ele) => {
          return (
            <tr>
              <td>{ele.customer}</td>
              <td>{ele.purchasecost}</td>
              <td>{ele.purchasedate}</td>
              <td>{CalculatePoint(ele.purchasecost)}</td>
            </tr>
          );
        })}
      </table>
    </div>
  );
};
export default Rewardspoint;
