import { useState, useEffect } from "react";
const Rewardspoint = () => {
  const [data, setData] = useState([]);
  useEffect(() => {
    setTimeout(() => {
      setData(dataset);
    }, 500);
  }, []);

  const CalculatePoint = (amount) => {
    let point = 0;
    if (amount > 50 && amount <= 100) {
      point += (amount - 50) * 1;
    } else if (amount > 100) {
      point += (amount - 100) * 2 + 50 * 1;
    } else {
      point += 0;
    }
    return point;
  };
  return (
    <div className="datatable">
      <table>
